import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

// let rows = [];

export default function BasicTable() {
    const [rows,updateState]=useState([])
    const classes = useStyles();

    // let rows=[];
    useEffect(() => {
      fetch("http://localhost:5000/generate-report/users")
        .then((data) => data.json())
        .then(response => {
            updateState(response);
        })
        .catch(err => {
            console.log(err)
        })
    }, [])
        
    
    const handelSubmit = data => {
        console.log("delete user submit button", data.target.value)
        const reqData={
          username: data.target.value
        }
        fetch(`http://localhost:5000/generate-report/delete-user`, {
        method: 'DELETE',
        headers: {'Content-Type':'application/json'},
        body: JSON.stringify(reqData)
        })
        .then(reqData => reqData.json())
        .then(response => {
            console.log("delete user success ", response)
            if(response){
              window.location.reload()
            }

        })
        .catch(err => 
            console.log("delete user error:",err)
        )
    }

  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Name</TableCell>
            <TableCell align="right">Username</TableCell>
            <TableCell align="right">Email</TableCell>
            <TableCell align="right">Phone Number</TableCell>
          </TableRow>
        </TableHead>
        
        <TableBody>
             { rows.map((row) => (
                 row.username!=="admin" &&
                    <TableRow key={row.name}>
                        <TableCell component="th" scope="row">{row.name}</TableCell>
                        <TableCell align="right">{row.username}</TableCell>
                        <TableCell align="right">{row.email}</TableCell>
                        <TableCell align="right">{row.phoneNumber}</TableCell>
                        <TableCell><button value={row.username} onClick={handelSubmit}>Delete</button></TableCell>
                    </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
