import React, { useState,useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

export default function BasicTable() {
    const [rows,updateState] = useState([])

    const classes = useStyles();

    // let rows=[];

    useEffect(() => {
      fetch("http://localhost:5000/generate-report/rooms")
      .then((data) => data.json())
      .then(response => {
          console.log(response)
          updateState(response);
      })
      .catch(err => {
          console.log(err)
      })
    }, [])
        
    const handelSubmit = data => {
      console.log("delete room submit button", data.target)
      const reqData={
        roomnumber: data
      }
        fetch(`http://localhost:5000/generate-report/delete-room`, {
        method: 'DELETE',
        headers: {'Content-Type':'application/json'},
        body: JSON.stringify(reqData)
        })
        .then(reqData => reqData.json())
        .then(response => {
            console.log("delete room success ", response)
        })
        .catch(err => 
            console.log("delete room error:",err)
        )
    }
    
  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Room Number</TableCell>
            <TableCell >Room Type</TableCell>
            <TableCell >Room Floor</TableCell>
            <TableCell> </TableCell>
          </TableRow>
        </TableHead>
        
        <TableBody>
             { rows.map((row) => (
                    <TableRow key={row.roomnumber}>
                        <TableCell component="th" scope="row">{row.roomnumber}</TableCell>
                        <TableCell component="th" scope="row" >{row.roomtype}</TableCell>
                        <TableCell>{row.roomfloor}</TableCell>
                        <TableCell><button value={row.roomnumber} onClick={handelSubmit}>Delete</button></TableCell>
                    </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
